
public class VariableDecDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub - Execution start
		
		int speedLimit = 80;
		
		
		int speed;
		speed = 100;
		 
		
		float b =2.2F; //float cast (float) 2.2 or 2.2F
		
		char c = 'F';
		
		String strTest = "Hello World!!!";
		
		double myDoubleValue = 3.55e2;//3.55*10^2 //3.55*100
		
		System.out.println("SPEED LIMIT VALUE=>>>>"+speedLimit+" speed : "+speed);
		
		
		System.out.println("float b =>>>>"+b);
		
		System.out.println("char c =>>>>"+c);
		
		
		System.out.println("String strtest =>"+strTest);
		
		
		System.out.println("double myDoubleValue =>"+myDoubleValue);
		

	}

}
